import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { Card, Button, Alert, Form } from "react-bootstrap";
import PetList from "./PetList";

const STATUS = {
  AVAILABLE: "available",
  SOLD: "sold",
  PENDING: "pending",
};

export default function Dashboard() {
  const [error, setError] = useState("");
  const [selectOption, setSelectOption] = useState(STATUS.AVAILABLE);
  const { logout } = useAuth();
  const navigateTo = useNavigate();

  async function logoutUser() {
    setError("");
    try {
      await logout();
      navigateTo("/");
    } catch {
      setError("Failed to log out");
    }
  }

  // const petStatus = [STATUS.AVAILABLE, STATUS.SOLD, STATUS.PENDING];
  function showOptions(e) {
    setSelectOption(e.target.value);
  }

  return (
    <div>
      Dashboard
      <SelectPets onChange={showOptions} options={STATUS}>
        <option value={STATUS.AVAILABLE}>{STATUS.AVAILABLE}</option>
        <option value={STATUS.SOLD}>{STATUS.SOLD}</option>
        <option value={STATUS.PENDING}>{STATUS.PENDING}</option>
      </SelectPets>
      <p>{selectOption}</p>
      {/* <PetList status={selectOption} /> */}
      <Button variant="link" onClick={logoutUser}>
        Log Out
        {error && <Alert variant="danger">{error}</Alert>}
      </Button>
    </div>
  );
}
